import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';

// Pages
import SubmitTicket from './pages/SubmitTicket';
import AdminDashboard from './pages/AdminDashboard';
import TicketDetail from './pages/TicketDetail';
import AuthPage from './pages/AuthPage';
import UserDashboard from './pages/UserDashboard';

// Components
import ChatBot from './components/ChatBot';
import Header from './components/Header';

// Context
import { AuthProvider } from './context/AuthContext';
import { TicketsProvider } from './context/TicketsContext';
import { ChatProvider } from './context/ChatContext';

// Protected route component
const ProtectedRoute = ({ children }) => {
  // Check if user is authenticated - this would typically use a more robust auth check
  const isAuthenticated = localStorage.getItem('auth_token');
  
  if (!isAuthenticated) {
    // Redirect to login if not authenticated
    return <Navigate to="/auth" replace />;
  }
  
  return children;
};

// Admin route component
const AdminRoute = ({ children }) => {
  // Check if user is authenticated and is an admin
  const isAuthenticated = localStorage.getItem('auth_token');
  const userData = localStorage.getItem('auth_user');
  let isAdmin = false;
  
  if (isAuthenticated && userData) {
    try {
      const user = JSON.parse(userData);
      isAdmin = user.role === 'admin';
    } catch (e) {
      // Invalid JSON, not admin
    }
  }
  
  if (!isAuthenticated) {
    // Redirect to login if not authenticated
    return <Navigate to="/auth" replace />;
  }
  
  if (!isAdmin) {
    // Redirect to home if not admin
    return <Navigate to="/" replace />;
  }
  
  return children;
};

function App() {
  // Check if user is logged in
  const isAuthenticated = localStorage.getItem('auth_token');
  const userData = localStorage.getItem('auth_user');
  let isAdmin = false;
  
  if (isAuthenticated && userData) {
    try {
      const user = JSON.parse(userData);
      isAdmin = user.role === 'admin';
    } catch (e) {
      // Invalid JSON, not admin
    }
  }
  
  return (
    <AuthProvider>
      <TicketsProvider>
        <ChatProvider>
          <div className="min-h-screen bg-gray-50 flex flex-col">
            <Routes>
              {/* Auth page has its own layout without header */}
              <Route path="/auth" element={<AuthPage />} />
              
              {/* Home route - redirects to appropriate dashboard if logged in */}
              <Route path="/" element={
                isAuthenticated ? (
                  isAdmin ? (
                    <Navigate to="/admin" replace />
                  ) : (
                    <Navigate to="/dashboard" replace />
                  )
                ) : (
                  <>
                    <Header />
                    <main className="flex-grow">
                      <SubmitTicket />
                    </main>
                    <ChatBot />
                  </>
                )
              } />
              
              {/* User dashboard for regular users */}
              <Route 
                path="/dashboard" 
                element={
                  <ProtectedRoute>
                    <Header />
                    <main className="flex-grow">
                      <UserDashboard />
                    </main>
                    <ChatBot />
                  </ProtectedRoute>
                } 
              />
              
              {/* Submit ticket page for logged in users */}
              <Route 
                path="/submit-ticket" 
                element={
                  <ProtectedRoute>
                    <Header />
                    <main className="flex-grow">
                      <SubmitTicket />
                    </main>
                    <ChatBot />
                  </ProtectedRoute>
                } 
              />
              
              {/* Protected admin routes */}
              <Route 
                path="/admin" 
                element={
                  <AdminRoute>
                    <Header />
                    <main className="flex-grow">
                      <AdminDashboard />
                    </main>
                    <ChatBot />
                  </AdminRoute>
                } 
              />
              
              <Route 
                path="/admin/tickets/:id" 
                element={
                  <AdminRoute>
                    <Header />
                    <main className="flex-grow">
                      <TicketDetail />
                    </main>
                    <ChatBot />
                  </AdminRoute>
                } 
              />
              
              {/* Fallback route */}
              <Route path="*" element={<Navigate to="/" replace />} />
            </Routes>
          </div>
        </ChatProvider>
      </TicketsProvider>
    </AuthProvider>
  );
}

export default App;
