import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from './AuthContext';

// Create context
const ChatContext = createContext();

// Custom hook to use the chat context
export const useChat = () => useContext(ChatContext);

export const ChatProvider = ({ children }) => {
  const { getAuthHeader } = useAuth();
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [connected, setConnected] = useState(true);

  // Check if API is available
  useEffect(() => {
    const checkConnection = async () => {
      try {
        await axios.get('/api/health');
        setConnected(true);
      } catch (error) {
        console.error('API connection error:', error);
        setConnected(false);
      }
    };

    checkConnection();
    const interval = setInterval(checkConnection, 30000); // Check every 30 seconds
    
    return () => clearInterval(interval);
  }, []);

  // Initial greeting message
  useEffect(() => {
    // Add initial bot message with slight delay to simulate bot thinking
    const timer = setTimeout(() => {
      setMessages([
        {
          text: "Hello! I'm your Syntax Samurai support assistant. How can I help you with your tech issue today?",
          sender: 'bot',
          timestamp: new Date().toISOString()
        }
      ]);
    }, 1000);
    
    return () => clearTimeout(timer);
  }, []);

  // Send message to the chatbot
  const sendMessage = async (text) => {
    if (!text.trim()) return;
    
    // Create message ID for tracking
    const messageId = `msg_${Date.now()}`;
    
    // Add user message to chat
    const userMessage = {
      id: messageId,
      text,
      sender: 'user',
      timestamp: new Date().toISOString()
    };
    
    setMessages(prev => [...prev, userMessage]);
    setLoading(true);
    setError(null);
    
    try {
      // Get chat history for context
      const history = messages
        .slice(-5)
        .map(msg => ({
          text: msg.text,
          sender: msg.sender,
          timestamp: msg.timestamp
        }));
      
      // Create a session ID if not exists
      if (!localStorage.getItem('chatSessionId')) {
        localStorage.setItem('chatSessionId', `session_${Date.now()}`);
      }
      
      // Call the API with proper parameters
      const response = await axios.post('/api/chat', {
        message: text,
        history: history,
        language: 'en',  // Default to English
        session_id: localStorage.getItem('chatSessionId')
      }, {
        headers: {
          ...getAuthHeader(),
          'Content-Type': 'application/json'
        }
      });
      
      // Process response
      const botMessage = {
        id: `resp_${Date.now()}`,
        text: response.data.message,
        sender: 'bot',
        timestamp: new Date().toISOString(),
        category: response.data.category,
        priority: response.data.priority,
        suggestions: response.data.suggestions
      };
      
      // Add typing indicator
      setMessages(prev => [...prev, { 
        id: 'typing', 
        text: '...', 
        sender: 'bot', 
        isTyping: true,
        timestamp: new Date().toISOString() 
      }]);
      
      // Simulate bot typing delay
      setTimeout(() => {
        setMessages(prev => prev.filter(msg => !msg.isTyping).concat([botMessage]));
        setLoading(false);
        
        // If the response suggests creating a ticket for critical issues
        if (response.data.create_ticket) {
          setTimeout(() => {
            setMessages(prev => [...prev, {
              id: `suggestion_${Date.now()}`,
              text: "Would you like me to create a support ticket for this issue? It appears to be a high priority matter.",
              sender: 'bot',
              isTicketSuggestion: true,
              timestamp: new Date().toISOString()
            }]);
          }, 1000);
        }
      }, 1500);
      
    } catch (err) {
      console.error('Chat API error:', err);
      setLoading(false);
      
      // Remove typing indicator if present
      setMessages(prev => prev.filter(msg => !msg.isTyping));
      
      // Add fallback response message
      const fallbackMessage = {
        id: `error_${Date.now()}`,
        text: connected ? 
          "I'm having trouble processing your request right now. Let me try a simpler response." :
          "I'm currently experiencing connection issues. Please try again later or submit a support ticket.",
        sender: 'bot',
        isError: true,
        timestamp: new Date().toISOString()
      };
      
      setMessages(prev => [...prev, fallbackMessage]);
      
      // If we have a connection but the API call failed, provide a basic response
      if (connected) {
        setTimeout(() => {
          // Basic fallback logic
          const lowerText = text.toLowerCase();
          let responseText = "I'm sorry, I couldn't process your request properly. Could you try rephrasing or providing more details?";
          
          if (lowerText.includes('error') || lowerText.includes('bug') || lowerText.includes('issue')) {
            responseText = "I understand you're experiencing an issue. Can you tell me what steps you've taken so far to resolve it?";
          } else if (lowerText.includes('install') || lowerText.includes('download')) {
            responseText = "Are you having trouble with installation? Please make sure your system meets the minimum requirements.";
          } else if (lowerText.includes('password') || lowerText.includes('login')) {
            responseText = "For account-related issues, you can try resetting your password through the 'Forgot Password' link.";
          }
          
          setMessages(prev => [...prev, {
            id: `fallback_${Date.now()}`,
            text: responseText,
            sender: 'bot',
            isFallback: true,
            timestamp: new Date().toISOString()
          }]);
        }, 1000);
      }
    }
  };

  // Clear chat history
  const clearChat = () => {
    setMessages([{
      id: `welcome_${Date.now()}`,
      text: "Hello! I'm your Syntax Samurai support assistant. How can I help you with your tech issue today?",
      sender: 'bot',
      timestamp: new Date().toISOString()
    }]);
    // Reset chat session
    localStorage.removeItem('chatSessionId');
  };

  // Context value
  const value = {
    messages,
    loading,
    error,
    connected,
    sendMessage,
    clearChat
  };

  return (
    <ChatContext.Provider value={value}>
      {children}
    </ChatContext.Provider>
  );
};
