import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from './AuthContext';

// Create context
const TicketsContext = createContext();

// Custom hook to use the tickets context
export const useTickets = () => useContext(TicketsContext);

export const TicketsProvider = ({ children }) => {
  const { getAuthHeader, isAuthenticated } = useAuth();
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [stats, setStats] = useState({
    totalTickets: 0,
    byCategory: {},
    byPriority: {},
  });

  // Load tickets if authenticated (admin only)
  useEffect(() => {
    if (isAuthenticated) {
      fetchTickets();
    }
  }, [isAuthenticated]);

  // Fetch all tickets
  const fetchTickets = async () => {
    setLoading(true);
    setError(null);
    
    try {
      // This would normally call your backend API
      // For demo purposes, we'll use mock data
      // const response = await axios.get('/api/tickets', { headers: getAuthHeader() });
      
      // Mock data for demonstration
      const mockTickets = [
        {
          ticket_id: 'T1001',
          subject: 'Application keeps crashing',
          text: 'The application keeps crashing when I try to save my work. This is very frustrating as I have lost hours of work.',
          category: 'bug',
          priority: 'critical',
          customer_name: 'John Smith',
          customer_id: 'C12345',
          created_at: '2023-06-10T14:23:45Z',
          status: 'open',
          assigned_to: null
        },
        {
          ticket_id: 'T1002',
          subject: 'How do I export data to PDF?',
          text: 'I need to export my data to PDF format. Where is this option located?',
          category: 'query',
          priority: 'low',
          customer_name: 'Emily Johnson',
          customer_id: 'C45678',
          created_at: '2023-06-11T09:15:22Z',
          status: 'open',
          assigned_to: null
        },
        {
          ticket_id: 'T1003',
          subject: 'Login page not working in Firefox',
          text: 'I cannot log in when using Firefox browser. The login button does nothing when clicked.',
          category: 'bug',
          priority: 'high',
          customer_name: 'Michael Wong',
          customer_id: 'C98765',
          created_at: '2023-06-12T16:45:30Z',
          status: 'in_progress',
          assigned_to: 'admin'
        },
        {
          ticket_id: 'T1004',
          subject: 'Feature request: Dark mode',
          text: 'It would be great if you could add a dark mode to reduce eye strain during night usage.',
          category: 'feature',
          priority: 'medium',
          customer_name: 'Sarah Davis',
          customer_id: 'C54321',
          created_at: '2023-06-13T11:30:15Z',
          status: 'open',
          assigned_to: null
        },
        {
          ticket_id: 'T1005',
          subject: 'Security vulnerability in password reset',
          text: 'I found a security vulnerability in the password reset functionality. It allows for bypassing email verification.',
          category: 'bug',
          priority: 'critical',
          customer_name: 'Alex Turner',
          customer_id: 'C13579',
          created_at: '2023-06-14T08:20:10Z',
          status: 'in_progress',
          assigned_to: 'admin'
        }
      ];
      
      setTickets(mockTickets);
      
      // Calculate statistics
      calculateStats(mockTickets);
    } catch (err) {
      console.error('Error fetching tickets:', err);
      setError('Failed to load tickets. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Calculate ticket statistics
  const calculateStats = (ticketData) => {
    const total = ticketData.length;
    
    // Count by category
    const categoryCount = ticketData.reduce((acc, ticket) => {
      const category = ticket.category || 'unknown';
      acc[category] = (acc[category] || 0) + 1;
      return acc;
    }, {});
    
    // Count by priority
    const priorityCount = ticketData.reduce((acc, ticket) => {
      const priority = ticket.priority || 'unknown';
      acc[priority] = (acc[priority] || 0) + 1;
      return acc;
    }, {});
    
    setStats({
      totalTickets: total,
      byCategory: categoryCount,
      byPriority: priorityCount,
    });
  };

  // Submit a new ticket
  const submitTicket = async (ticketData) => {
    setLoading(true);
    setError(null);
    
    try {
      // In a real application, this would call your backend API
      const response = await axios.post('/api/tickets', ticketData);
      return response.data;
    } catch (err) {
      console.error('Error submitting ticket:', err);
      setError('Failed to submit ticket. Please try again.');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Get a specific ticket
  const getTicket = async (ticketId) => {
    setLoading(true);
    setError(null);
    
    try {
      // In a production app, this would call your backend API
      // For demo purposes, we'll filter from our mock data
      
      const ticket = tickets.find(t => t.ticket_id === ticketId);
      
      if (!ticket) {
        throw new Error('Ticket not found');
      }
      
      return ticket;
    } catch (err) {
      console.error(`Error fetching ticket ${ticketId}:`, err);
      setError('Failed to load ticket details. Please try again.');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Update a ticket
  const updateTicket = async (ticketId, updates) => {
    setLoading(true);
    setError(null);
    
    try {
      // In a real application, this would call your backend API
      // For demo purposes, we'll update our local state
      
      const updatedTickets = tickets.map(ticket => 
        ticket.ticket_id === ticketId ? { ...ticket, ...updates } : ticket
      );
      
      setTickets(updatedTickets);
      calculateStats(updatedTickets);
      
      return { ticket_id: ticketId, ...updates };
    } catch (err) {
      console.error(`Error updating ticket ${ticketId}:`, err);
      setError('Failed to update ticket. Please try again.');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Assign a ticket
  const assignTicket = async (ticketId, userId) => {
    return updateTicket(ticketId, { 
      assigned_to: userId,
      status: 'in_progress'
    });
  };

  // Context value
  const value = {
    tickets,
    loading,
    error,
    stats,
    fetchTickets,
    submitTicket,
    getTicket,
    updateTicket,
    assignTicket,
  };

  return (
    <TicketsContext.Provider value={value}>
      {children}
    </TicketsContext.Provider>
  );
};
